export default function PaymentForm() {
    const history = useHistory(); // Inicializa useHistory
    const context = useContext(ShopContext);
    const [success, setSuccess] = useState(false);
    const stripe = useStripe();
    const elements = useElements();

    const handleSubmit = async (e) => {
      e.preventDefault();
      const { error, paymentMethod } = await stripe.createPaymentMethod({
        type: 'card',
        card: elements.getElement(CardElement),
      });

      if (!error) {
        try {
          const { id } = paymentMethod;
          const response = await axios.post('http://localhost:3001/payment', {
            amount: context.payAmount,
            id,
          });

          if (response.data.success) {
            alert('Payment success');
            setSuccess(true);
            history.push('/'); // Redirige a la página principal después del éxito del pago
          }
        } catch (error) {
          console.log('Error', error);
        }
      } else {
        console.log(error.message);
      }
    }

    return (
        <>
        {!success ?
        <div className="ButtonsContainer">
            <form onSubmit={handleSubmit}>
                <fieldset className="FormGroup">
                    <h4>Enter your card details to complete the purchase</h4>
                    <div Ingrese TarjetaclassName="FormRow">
                        <CardElement options={CARD_OPTIONS}/>
                    </div>
                </fieldset>
                <button className="pay" onClick={handleSubmit}>Pay 💸</button> {/*boton para pagar  */}
            </form>
        </div>
        :
        <div>
            {console.log("succesful payment")}
        </div>
        }
        </>
    )
}
